mapper.select:
select

from orderinfo


    and OrderNO like #{OrderNo,jdbcType=VARCHAR}


    and BatchNO like #{BacketNo,jdbcType=VARCHAR}


    and ProNO like #{ProNo,jdbcType=VARCHAR}


    and ProName like #{Proname,jdbcType=VARCHAR}


    and FacNo = #{facno,jdbcType=VARCHAR}


order by OrderNO asc
